import { createFeatureSelector } from '@ngrx/store';
import { routerReducer } from '@ngrx/router-store';
import { environment } from '../../environments/environment';
import { initStateFromLocalStorage } from './meta-reducers/init-state-from-localstorage.reducer';
import { debug } from './meta-reducers/debug.reducer';
import { authReducer } from './auth/auth.reducer';
import { settingsReducer } from './settings/settings.reducer';
export const reducers = {
  auth: authReducer,
  settings: settingsReducer,
  router: routerReducer
};
export const metaReducers = [initStateFromLocalStorage];

if (!environment.production) {
  if (!environment.test) {
    metaReducers.unshift(debug);
  }
}

export const selectAuthState = createFeatureSelector('auth');
export const selectSettingsState = createFeatureSelector('settings');
export const selectRouterState = createFeatureSelector('router');
